import React, { Fragment, useState, useEffect } from 'react';
import { Image, Checkbox, Typography, Spin } from 'antd';
import { LoadingOutlined } from '@ant-design/icons';
import { getAlbums } from '@/services/tcb';

export default (props): React.ReactNode => {
  const [albums, setAlumbs] = useState([]);
  const [loading, setLoading] = useState(false);
  const { reloadAlbums, mode, selectedAlbums, setSelectedAlbums, editAlbum } = props;
  useEffect(() => {
    const getAllAlbums = async () => {
      setLoading(true);
      const res = await getAlbums({});
      setAlumbs(res)
      setLoading(false);
    }
    getAllAlbums();
  }, [reloadAlbums])
  useEffect(() => {
    if (mode === 'single') {
      setSelectedAlbums([])
    }
  },[mode])
  const handleChange = (e, album) => {
    const {id, name, description1 = '', description2 = ''} = album;
    if (mode === 'batch') {
      if (!selectedAlbums.filter(sa => sa === id).length) {
        setSelectedAlbums([
          ...selectedAlbums,
          id,
        ]);
      } else {
        setSelectedAlbums(selectedAlbums.filter(sa => sa !== id))
      }
    } else {
      editAlbum({
        id,
        name,
        description1,
        description2,
      });
    }
    return false;
  }
  return (
    <div className="album-list">
      {!loading && albums.map((album) => (
        <div className={`album-wrap ${selectedAlbums.includes(album.id) ? 'checked' : ''}`} key={album.id}>
          {album.name ? (
            <Fragment>
              <Image
                width={204}
                height={204}
                src={`${album.cover}?&imageMogr2/thumbnail/200x200`}
                placeholder={
                  <div className="album-loading"><LoadingOutlined loading="true" /></div>
                }
                preview={false}
                fallback="data:image/png;base64,"
                alt={album.name}
                onClick={(e) => {
                  handleChange(e,
                    {
                      id: album.id,
                      name: album.name,
                      description1: album.description1,
                      description2: album.description2
                    }
                  )
                }}
              />
              <Checkbox checked={selectedAlbums.includes(album.id)} onChange={(e) => { handleChange(e, album.id) }} />
            </Fragment>
          ) : null}
          <Typography>{album.name}</Typography>
          <Typography>{album.description1}</Typography>
          <Typography>{album.description2}</Typography>
        </div>
      ))}
      {loading && <Spin
        size="small"
        style={{
          marginLeft: 8,
          marginRight: 8,
        }}
      />}
    </div>
  );
}
